import {NativeModules} from 'react-native';

const HikeAppStateModule = NativeModules.HikeAppStateModule;

/**
 * Hike App State Module
 */
class HikeAppState {

  /**
   * Toggle Block mode for app.
   * @param {Boolean} toBlock True to block. False to unblock.
   */
  static toggleBlock(toBlock) { return HikeAppStateModule.toggleBlock(toBlock); }

  /**
   * Toggle notifications for the app.
   * @param {Boolean} toEnable True to enable notifications. False to disable(mute app).
   */
  static toggleNotifications(toEnable) { return HikeAppStateModule.toggleNotifications(toEnable); }

  /**
   * Exit microapp.
   */
  static exitApp() { return HikeAppStateModule.exitApp(); }

}

export default HikeAppState;
