import { NativeModules } from 'react-native';
const HikePayModule = NativeModules.HikePayModule;

const DEVICE = true;

class HikePay {
    static makePayment(checkoutData) {
			if (DEVICE) {
				return HikePayModule.makePayment(checkoutData);
        }
        return new Promise((resolve) => {
            setTimeout(resolve, 500, [{
                "status": "PAYMENT_SUCCESS",
                "action": "SEND_TO_MERCHANT",
                "actionParams": {
                    "wallet": {
                        "name": "+911111144444",
                        "walletMoneyList": [{
                            "currency": "INR",
                            "amount": 10110,
                            "displayCurrency": "INR",
                            "conversionFactor": 1
                        }]
                    },
                    "merchantTransactionId": "RC0012051749",
                    "transaction": {
                        "transactionId": "TXN_T001205490",
                        "transactionType": "DEBIT",
                        "currency": "INR",
                        "amount": 10,
                        "transactionDate": 1483943355448,
                        "transactionMessage": "SUCCESS",
                        "userMessage": "Payment made to merchant : merchantName",
                        "transactionStatus": "SUCCESS"
                    }
                }
            }]);
        });
    }
}

export default HikePay;
