import React, { Component } from 'react';
import { View } from 'react-native';
import AppTheme from '../../appthemes/AppTheme';
import ThemeText from '../../appthemes/components/ThemeText';
import ThemeSolidButton from '../../appthemes/components/ThemeSolidButton';

class SendView extends Component {
  constructor(props){
    super(props);
    this.bottom=props.bottom ? props.bottom :0;
  }
  render() {
    return (
      <View style={{flexDirection: 'column', backgroundColor: AppTheme.getColorPalette().backgroundColor,paddingBottom:this.bottom}}>
        <View style={{height: 0.5, backgroundColor: AppTheme.getColorPalette().separatorColor}}>
        </View>
        <View style={{alignItems: 'center', justifyContent: 'space-between', flexDirection: 'row', height: 60, paddingLeft: 16, paddingRight: 16}}>
          <View style={{flex: 0.75, flexDirection: 'column'}}>
            <ThemeText
            fontProfile='FontProfile10'
            text={this.props.title}
            color={AppTheme.getColorPalette().secondaryColor}
            />
            {(this.props.subTitle) ? 
            <ThemeText
            fontProfile='FontProfile10'
            text={this.props.subTitle}
            color={AppTheme.getColorPalette().secondaryColor}
            /> : null}
          </View>

          <View style={{flex: 0.25}}>
            <ThemeSolidButton
                style={{marginTop: 2, height: 32}}
                  onPress={() => {
                    this.props.onSendClicked()
                  }} 
                  title={this.props.sendBtnText}
                  loading={this.props.showButtonLoader}
                  textColorStateProfile='ColorStateProfile05'
                  buttonProfile={this.props.isEnabled ? 'ButtonProfile02' : 'ButtonProfile13'}
                  fontProfile='FontProfile12'
              />
          </View>
        </View>
      </View>
    );
  }
}

module.exports = {
  SendView
}